import axios from "axios";
import { Component } from "react";

export default class TodosCom extends Component
{
    state={
        todos:[],
        isEditMode:false,
        todoObj:{
            title:"",
            body:"",
            userId:1,
        },
    };

        onClickHandler=(id)=>{
            axios.delete("https://jsonplaceholder.typicode.com/todos/" +id).then((response)=>{
                console.log(response);
                this.FetchMethod();
                alert("Deleted");


            });
        }
        onChangeHandler = (event) => {
            const {name,value}=event.target;
            const todoObjectCopy=this.state.todoObj;
            todoObjectCopy[name]=value;
            this.setState({todoObj:todoObjectCopy});
        };
        onEditClickHandler = (id) => {
            const editObject=this.state.todos.find((todo) => todo.id === id);
            if(editObject)
            {
                this.setState({
                    todoObj:editObject,isEditMode:true
                });
            }
        };
        resetState ()
        {
            this.setState({
                todoObj:{
                    title:"",
                    body:"",
                    userId:1,
                },
                isEditMode:false,
            });
        }
        onCancelClickHandler = (event) =>{
            event.preventDefault();
            this.resetState();
        }
        onFormSubmitClick = (event) => {
            event.preventDefault();
            if(this.state.isEditMode)
            {
                axios.put("https://jsonplaceholder.typicode.com/todos/"+this.state.todoObj.id,this.state)
            }
        }
    render()
    {
        return(
            <>
                <h1>In Todos</h1>
                {this.state.todos.map((todo,index)=>(
                    <div key={index}>
                        <div>
                        {index+1}.{todo.title}
                        </div>
                        <br/>
                        <button onClick={()=>{
                    this.onClickHandler(todo.id); }}>Delete</button>
                    </div>

                     ))}
                </>
        )
    }
    componentDidMount()
    {
        this.FetchMethod();
    }

    FetchMethod() {
        axios.get("https://jsonplaceholder.typicode.com/todos").then((response) => {
            console.log(response.data);
            this.setState({ todos: response.data });
        });
    }
}
