import axios from "axios";
import { Component } from "react";

export default class PostComponent extends Component
{
    state={
        posts:[],
        postObj:{
            title:"",
            body:"",
        },
    };
    onClickHandler=(id)=>{
        axios.delete("https://jsonplaceholder.typicode.com/posts/" +id).then((response)=>{
            console.log(response);
            this.FetchMethod();
            alert("Deleted");
        });
    }
    onChangeHandler = (event) => 
    {
        const {name,value} =event.target;
        const postObjectCopy=this.state.postObj;
        postObjectCopy[name]=value;
        this.setState({postObj:postObjectCopy});
    };
    onFormSubmitClick = (event) =>{
        event
    }
    render()
    {
        return(

            <>
                <h1>In Post</h1>

                {this.state.posts.map((post,index)=> (
                    <div key={index}>
                        <div>
                            {index+1}.{post.title}
                            {/* <br/>
                            {index+1}.{post.body} */}
                        </div>
                        <br/>
                        <button onClick={()=>{
                    this.onClickHandler(post.id);
                }}
                >
                    Delete</button>
                    </div>
                ))}

            </>
        )
    }
    componentDidMount()
    {
        this.FetchMethod();

    }

    FetchMethod() {
        axios.get("https://jsonplaceholder.typicode.com/posts").then((response) => {
            console.log(response.data);
            this.setState({ posts: response.data });
        });
    }
}
